import { createSlice } from '@reduxjs/toolkit';
import { countCalc } from 'client/actions/counterAction';

export type countState = {
  count: number;
  loading: boolean;
};

const initialState: countState = {
  count: 0,
  loading: false,
};
export const countSlice = createSlice({
  name: 'count',
  initialState,
  reducers: {},
  extraReducers: {
    [countCalc.pending.type]: (state) => {
      // eslint-disable-next-line no-param-reassign
      state.loading = true;
    },
    [countCalc.fulfilled.type]: (state, { payload }: any) => {
      const { count } = payload;

      // eslint-disable-next-line no-param-reassign
      state.count = count;
    },

    [countCalc.rejected.type]: (state) => {
      // eslint-disable-next-line no-param-reassign
      state.loading = false;
    },
  },
});

export default countSlice.reducer;
